import React from "react";
import Navbar from "./NavBar";
import Exams from "./Exams";
class ChooseRole extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userrole: '',
        };
    }

    handleSubmit = (e) => {
        this.setState({
            userrole: e.target.value,
        }, () => {
            console.log(this.state.userrole);
        });
    }

    render() {
        return (
            <>
         {this.state.userrole===''  &&
         <select name="user-role" id='user-role'defaultValue='' onChange={this.handleSubmit}>
                  <option value=''>Select Role</option>
                <option value={'SuperAdmin'}>Super Admin</option>
                <option value={'Admin'}>Admin</option>
            </select>}
            {this.state.userrole === 'SuperAdmin' && <Navbar/>}
            {this.state.userrole === 'Admin' && <Exams User ={this.state.userrole}/>}
        </>
        );

    }
}
export default ChooseRole;
