import { createSlice } from "@reduxjs/toolkit";

export const Role = createSlice({
    name:'Role',
    initialState:'',
    reducers:{
        Admin : state=>{
            state.value = 'Admin'
        },
        SuperAdmin : state=>{
            state = 'SuperAdmin'
        }

    }
})

export const {Admin , SuperAdmin} = Role.actions

export default Role.reducer
