import React from "react";
import Navbar from "./NavBar";
class ChooseRole extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userrole: '',
            slidebarpermission:false,
            ExamPermission:false,
        };
    }

    handleSubmit = (e) => {
        this.setState({
            userrole: e.target.value,
            slidebarpermission:this.userrole==='SuperAdmin',
            ExamPermission:true,
        }, () => {
            console.log(this.state.userrole);
        });
    }

    render() {
        return (
            <>
            <select name="user-role" id='user-role'defaultValue='' onChange={this.handleSubmit}>
                  <option value=''>Select Role</option>
                <option value={'SuperAdmin'}>Super Admin</option>
                <option value={'Admin'}>Admin</option>
            </select>
            {this.slidebarpermission?<Navbar/>:''}
        </>
        );

    }
}

export default ChooseRole;
